mapper.update:
UPDATE daul.chatbot_intent_list
   SET del_chk = 'Y'
 WHERE intent_id = #{id}

UPDATE daul.chatbot_intent_list
   SET title = #{text}
 WHERE intent_id = #{id}

mapper.select:
SELECT count(*)
  FROM chatbot_input
 WHERE intent_id = #{intent_id}

SELECT *
  FROM daul.chatbot_input
 WHERE intent_id = #{intent_id}

SELECT *
  FROM daul.chatbot_respone_attach
 WHERE parent_id = #{img_attach_id}

mapper.insert:
INSERT INTO daul.chatbot_respone_attach
	 (
	   parent_id
	 , org_file_name
	 , save_file_name
	 , file_size
	 , del_chk
	 , attach_type
	 , intent_id
	 )
VALUES
	 (
	   #{idx}
	 , #{org_file_name}
	 , #{save_file_name}
	 , #{file_size}
	 , 'N'
	 , #{attach_type}
	 , #{intent_id}
	 )
