mapper.select:
SELECT admin_contest_idx, create_user, title, content, DATE_FORMAT(contest_s_date, '%Y-%m-%d') as contest_s_date, DATE_FORMAT(contest_e_date, '%Y-%m-%d') as contest_e_date, DATE_FORMAT(submit_s_date, '%Y-%m-%d') as submit_s_date, DATE_FORMAT(submit_e_date, '%Y-%m-%d') as submit_e_date, create_date FROM daul.contest_admin WHERE del_chk != 'Y'
AND title like concat('%',#{search},'%')
AND content like concat('%',#{search},'%')
AND create_user like concat('%',#{search},'%')
AND create_date > #{search_s_date}
AND create_date < #{search_e_date}

mapper.insert:
INSERT INTO contest_admin_attach
	(
	   admin_contest_idx
     , org_file_name
     , save_file_name
     , file_size
     , create_user
     , del_chk
     , create_date
     , attach_type
	)
VALUES
	(
	   #{idx}
     , #{org_file_name}
     , #{save_file_name}
     , #{file_size}
     , #{create_user}
     , 'N'
     , SYSDATE()
     , #{attach_type}
	)

mapper.update:
UPDATE daul.contest_admin
   SET update_user = #{update_user}
     , update_date = SYSDATE()

     , title = #{title}


     , content = #{content}

     , contest_s_date = #{contest_s_date}
     , contest_e_date = #{contest_e_date}
     , submit_s_date = #{submit_s_date}
     , submit_e_date = #{submit_e_date}
 WHERE admin_contest_idx = #{admin_contest_idx}
