mapper.update:
UPDATE daul.out_user 
SET 

	email = #{email}


	pw = #{pw}


	,phone_num = #{phone_num}


	,nickname = #{nickname}

WHERE user_seq = #{user_seq}

mapper.insert:
INSERT INTO daul.out_user
	   (
	     email
	   , pw
	   , nickname
	   , phone_num
	   )
VALUES (
		 #{email}
	   , #{pw}
	   , #{nickname}
	   , #{phone_num}
	   )

mapper.delete:
DELETE FROM daul.out_user
 WHERE email = #{email}
   AND nickname = #{nickname}

mapper.select:
SELECT email
  FROM daul.out_user
 WHERE nickname = #{nickname}
   AND pw = #{pw}
