mapper.update:
UPDATE daul.user
   SET user_id = #{user_id}

     , pw = #{pw}


     , auth_type = #{auth_type}


     , name = #{name}


     , phone = #{phone}


     , email = #{email}


     , email_chk = #{email_chk}


     , talk_chk = #{talk_chk}


     , sms_chk = #{sms_chk}

 WHERE user_id = #{user_id}

mapper.insert:
INSERT INTO daul.user
	   (
